package emailer;import java.util.List;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.beans.factory.annotation.Qualifier;import org.springframework.stereotype.Component;import emailer.agenda.Agenda;import emailer.corretorortografico.CorretorOrtografico;@Componentpublic class Emailer {    private CorretorOrtografico corretorOrtografico;    private Agenda agenda;        @Autowired    public Emailer(@Qualifier("hashMapAgenda") Agenda agenda,                    @Qualifier("corretorIngles") CorretorOrtografico corretorOrtografico) {        this.corretorOrtografico = corretorOrtografico;        this.agenda = agenda;    }    public void enviar(List<String> nomes, String texto) {        try {            corretorOrtografico.verificarOrtografia(texto);            for (String nome : nomes) {                String email = agenda.consultar(nome);                if(email == null){                    throw new RuntimeException(String.format("'%s' n√£o foi encontrado na agenda!", nome));                }                System.out.println(texto);            }        } catch (Exception e) {            throw new EnvioException(e.getMessage());        }    }}